<?php		ini_set("display_errors", "On");	error_reporting(E_ALL | E_STRICT);	#获取服务器配置	include_once "servermgr.php";		$memo = isset($_GET['memo']) ? $_GET['memo'] : NULL;	$app_player_id = isset($_GET['app_player_id']) ? $_GET['app_player_id'] : NULL;	$serverid = $memo;		$playerid = $app_player_id;		$server = getServerCfg($serverid);	if (!$server) {		$data['status'] = 0;		$data['text'] = "not found server";		die(json_encode($data));	}	$host = 'http://' . $server['http_host'] . ':' . $server['http_port'];	$url=sprintf('/questionnaire?playerid=%d', $playerid);	$ret = file_get_contents($host . $url);		if ($ret == 1) {#成功		$data['status'] = 1;		$data['text'] = "suc";	} else {	    $data['status'] = 0;		$data['text'] = "failed";	}	echo json_encode($data);?>